import React, { useState } from 'react';
import { Container, Typography, Button, Grid, Select, MenuItem, FormControl, InputLabel, Paper } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import BackgroundImage from '../../assets/background.jpg'; // Assurez-vous que le chemin est correct

const LandingPage: React.FC = () => {
  const [ageRange, setAgeRange] = useState('');
  const [bodyType, setBodyType] = useState('');
  const navigate = useNavigate();

  const handleStart = () => {
    navigate('/login');
  };

  return (
    <div style={{ minHeight: '100vh', backgroundImage: `url(${BackgroundImage})`, backgroundSize: 'cover', backgroundPosition: 'center', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
      <Container 
        sx={{
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          color: '#fff',
          padding: '20px',
          backgroundColor: 'rgba(0, 0, 0, 0.5)', // Ajout d'un fond semi-transparent pour améliorer la lisibilité
          borderRadius: '10px'
        }}
      >
        <Typography variant="h4" gutterBottom>
          Bienvenue sur Fitness Tracker
        </Typography>
        <Typography variant="h6" gutterBottom>
          Veuillez choisir votre tranche d'âge et votre type de corps
        </Typography>

        <Grid container spacing={3} sx={{ marginTop: '20px' }}>
          <Grid item xs={12} sm={6}>
            <Paper elevation={3} sx={{ padding: '20px', backgroundColor: 'rgba(255, 165, 0, 0.8)', boxShadow: '0px 4px 20px rgba(0, 0, 0, 0.2)' }}>
              <FormControl fullWidth>
                <InputLabel style={{ color: '#fff' }}>Tranche d'âge</InputLabel>
                <Select 
                  value={ageRange} 
                  onChange={(e) => setAgeRange(e.target.value)}
                  style={{ color: '#fff' }}
                >
                  <MenuItem value="18-25">18-25 ans</MenuItem>
                  <MenuItem value="26-35">26-35 ans</MenuItem>
                  <MenuItem value="36-45">36-45 ans</MenuItem>
                  <MenuItem value="46+">46 ans et plus</MenuItem>
                </Select>
              </FormControl>
            </Paper>
          </Grid>

          <Grid item xs={12} sm={6}>
            <Paper elevation={3} sx={{ padding: '20px', backgroundColor: 'rgba(255, 165, 0, 0.8)', boxShadow: '0px 4px 20px rgba(0, 0, 0, 0.2)' }}>
              <FormControl fullWidth>
                <InputLabel style={{ color: '#fff' }}>Type de corps</InputLabel>
                <Select 
                  value={bodyType} 
                  onChange={(e) => setBodyType(e.target.value)}
                  style={{ color: '#fff' }}
                >
                  <MenuItem value="ectomorph">Ectomorphe</MenuItem>
                  <MenuItem value="mesomorph">Mésomorphe</MenuItem>
                  <MenuItem value="endomorph">Endomorphe</MenuItem>
                </Select>
              </FormControl>
            </Paper>
          </Grid>
        </Grid>

        <Button 
          variant="contained" 
          color="primary" 
          onClick={handleStart} 
          sx={{ marginTop: '30px' }}
          disabled={!ageRange || !bodyType} // Désactiver le bouton si les choix ne sont pas faits
        >
          Commencer
        </Button>
      </Container>
    </div>
  );
};

export default LandingPage;
