import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TextField, Button, Dialog, DialogActions, DialogContent, DialogTitle } from '@mui/material';

interface EditGoalProps {
  open: boolean;
  handleClose: () => void;
  goalId: string;
  onGoalUpdated: () => void;
}

const EditGoal: React.FC<EditGoalProps> = ({ open, handleClose, goalId, onGoalUpdated }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [targetDate, setTargetDate] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:5000/goals/${goalId}`)
      .then(response => {
        const { title, description, targetDate } = response.data;
        setTitle(title);
        setDescription(description);
        setTargetDate(targetDate);
      })
      .catch(error => {
        console.error('There was an error fetching the goal!', error);
      });
  }, [goalId]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const updatedGoal = { title, description, targetDate };

    axios.put(`http://localhost:5000/goals/${goalId}`, updatedGoal)
      .then(() => {
        onGoalUpdated();
        handleClose();
      })
      .catch(error => {
        console.error('There was an error updating the goal!', error);
      });
  };

  return (
    <Dialog open={open} onClose={handleClose}>
      <DialogTitle>Edit Goal</DialogTitle>
      <DialogContent>
        <form onSubmit={handleSubmit}>
          <TextField
            label="Title"
            fullWidth
            margin="normal"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <TextField
            label="Description"
            fullWidth
            margin="normal"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <TextField
            label="Target Date"
            type="date"
            fullWidth
            margin="normal"
            value={targetDate}
            onChange={(e) => setTargetDate(e.target.value)}
            InputLabelProps={{
              shrink: true,
            }}
          />
          <DialogActions>
            <Button onClick={handleClose} color="secondary">
              Cancel
            </Button>
            <Button type="submit" color="primary">
              Update
            </Button>
          </DialogActions>
        </form>
      </DialogContent>
    </Dialog>
  );
};

export default EditGoal;
