import React, { useState } from 'react';
import { Container, Typography, Grid, Card, CardContent, Select, MenuItem, FormControl, InputLabel } from '@mui/material';

// Type des exercices
type Exercise = {
  name: string;
  description: string;
  videoUrl: string;
};

// Type des exercices par type de corps
type WorkoutPlan = {
  ectomorph: Exercise[];
  mesomorph: Exercise[];
  endomorph: Exercise[];
};

// Exemple de données avec vidéos
const workouts: WorkoutPlan = {
  ectomorph: [
    { name: 'Push Ups', description: 'Do 3 sets of 15 push-ups.', videoUrl: 'https://www.youtube.com/embed/_l3ySVKYVJ8' },
    { name: 'Pull Ups', description: 'Do 3 sets of 10 pull-ups.', videoUrl: 'https://www.youtube.com/embed/eGo4IYlbE5g' },
  ],
  mesomorph: [
    { name: 'Squats', description: 'Do 3 sets of 20 squats.', videoUrl: 'https://www.youtube.com/embed/aclHkVaku9U' },
    { name: 'Lunges', description: 'Do 3 sets of 12 lunges on each leg.', videoUrl: 'https://www.youtube.com/embed/QOVaHwm-Q6U' },
  ],
  endomorph: [
    { name: 'Plank', description: 'Hold the plank for 1 minute.', videoUrl: 'https://www.youtube.com/embed/pSHjTRCQxIw' },
    { name: 'Jumping Jacks', description: 'Do 3 sets of 30 jumping jacks.', videoUrl: 'https://www.youtube.com/embed/c4DAnQ6DtF8' },
  ],
};

const Workout: React.FC = () => {
  const [bodyType, setBodyType] = useState<'ectomorph' | 'mesomorph' | 'endomorph'>('ectomorph');

  return (
    <div style={{ 
      height: '100vh', 
      backgroundColor: '#000', // Fond noir
      color: '#fff', // Texte blanc
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center', 
      padding: '20px' 
    }}>
      <Container 
        sx={{
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          color: '#fff',
          padding: '20px',
          backgroundColor: 'rgba(255, 255, 255, 0.1)', // Légère transparence pour faire ressortir les éléments
          borderRadius: '10px',
        }}
      >


        <Typography variant="h4" gutterBottom>
          Select Your Body Type and See the Exercises
        </Typography>

        {/* Sélection du type de corps */}
        <FormControl fullWidth sx={{ marginBottom: '20px' }}>
          <InputLabel sx={{ color: '#fff' }}>Body Type</InputLabel>
          <Select
            value={bodyType}
            onChange={(e) => setBodyType(e.target.value as 'ectomorph' | 'mesomorph' | 'endomorph')}
            label="Body Type"
            sx={{ backgroundColor: 'rgba(192, 192, 192, 0.8)', borderRadius: '5px' }}
          >
            <MenuItem value="ectomorph">Ectomorph</MenuItem>
            <MenuItem value="mesomorph">Mesomorph</MenuItem>
            <MenuItem value="endomorph">Endomorph</MenuItem>
          </Select>
        </FormControl>

        <Grid container spacing={3}>
          {workouts[bodyType].map((exercise, index) => (
            <Grid item xs={12} sm={6} key={index}>
              <Card sx={{ backgroundColor: '#333' }}> {/* Cartes avec fond sombre */}
                <CardContent>
                  <Typography variant="h6" sx={{ color: '#fff' }}>{exercise.name}</Typography>
                  <Typography sx={{ color: '#ccc' }}>{exercise.description}</Typography>
                  <iframe
                    width="100%"
                    height="200"
                    src={exercise.videoUrl}
                    title={exercise.name}
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                  ></iframe>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    </div>
  );
};

export default Workout;
