import React, { useState } from 'react';
import { Container, Typography, Button, Grid, Select, MenuItem, FormControl, InputLabel } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import BackgroundImage from '../../assets/background.jpg';

const LandingPage: React.FC = () => {
  const [ageRange, setAgeRange] = useState('');
  const [bodyType, setBodyType] = useState('');
  const navigate = useNavigate();

  const handleStart = () => {
    navigate('/login');
  };

  return (
    <div style={{ 
      height: '100vh', 
      backgroundImage: `url(${BackgroundImage})`, 
      backgroundSize: 'cover', 
      backgroundRepeat: 'no-repeat', 
      backgroundPosition: 'center', 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center' 
    }}>
      <Container 
        sx={{
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          color: '#fff',
          padding: '20px',
          backgroundColor: 'rgba(0, 0, 0, 0.6)', // Une légère opacité pour le conteneur
          borderRadius: '10px',
        }}
      >
        <Typography variant="h4" gutterBottom>
        Welcome to Fitness Tracker
        </Typography>
        <Typography variant="h6" gutterBottom>
        Please choose your age range and body type
        </Typography>

        <Grid container spacing={3} sx={{ marginTop: '20px' }}>
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth variant="outlined" sx={{ backgroundColor: 'rgba(192, 192, 192, 0.8)', borderRadius: '10px', padding: '0px' }}>
              <InputLabel>Age group</InputLabel>
              <Select
                value={ageRange}
                onChange={(e) => setAgeRange(e.target.value as string)}
                label="Tranche d'âge"
              >
                <MenuItem value="18-25">18-25</MenuItem>
                <MenuItem value="26-35">26-35</MenuItem>
                <MenuItem value="36-45">36-45</MenuItem>
                <MenuItem value="46+">46+</MenuItem>
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth variant="outlined" sx={{ backgroundColor: 'rgba(192, 192, 192, 0.8)', borderRadius: '10px', padding: '10px' }}>
              <InputLabel>Body type</InputLabel>
              <Select
                value={bodyType}
                onChange={(e) => setBodyType(e.target.value as string)}
                label="Type de corps"
              >
                <MenuItem value="ectomorph">Ectomorphe</MenuItem>
                <MenuItem value="mesomorph">Mésomorphe</MenuItem>
                <MenuItem value="endomorph">Endomorphe</MenuItem>
              </Select>
            </FormControl>
          </Grid>
        </Grid>

        <Button 
          variant="contained" 
          sx={{ marginTop: '30px', backgroundColor: '#FF9800', color: '#fff', '&:hover': { backgroundColor: '#e68900' } }}
          onClick={handleStart}
        >
          <b>Let's start</b>
        </Button>
      </Container>
    </div>
  );
};

export default LandingPage;
