import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Typography, List, ListItem, ListItemText, ListItemSecondaryAction, IconButton, Button } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddWorkout from './AddWorkout';
import EditWorkout from './EditWorkout';

interface Workout {
  _id: string;
  type: string;
  duration: number;
  caloriesBurned: number;
}

const Workouts: React.FC = () => {
  const [workouts, setWorkouts] = useState<Workout[]>([]);
  const [addWorkoutOpen, setAddWorkoutOpen] = useState(false);
  const [editWorkoutOpen, setEditWorkoutOpen] = useState(false);
  const [currentWorkoutId, setCurrentWorkoutId] = useState<string | null>(null);

  useEffect(() => {
    fetchWorkouts();
  }, []);

  const fetchWorkouts = () => {
    axios.get('http://localhost:5000/workouts')
      .then(response => {
        setWorkouts(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the workouts!', error);
      });
  };

  const handleDelete = (id: string) => {
    axios.delete(`http://localhost:5000/workouts/${id}`)
      .then(() => {
        fetchWorkouts();
      })
      .catch(error => {
        console.error('There was an error deleting the workout!', error);
      });
  };

  const handleEdit = (id: string) => {
    setCurrentWorkoutId(id);
    setEditWorkoutOpen(true);
  };

  return (
    <Container>
      <Typography variant="h4" component="h1" gutterBottom>
        Workouts
      </Typography>
      <Button variant="contained" color="primary" onClick={() => setAddWorkoutOpen(true)}>
        Add Workout
      </Button>
      <List>
        {workouts.map(workout => (
          <ListItem key={workout._id}>
            <ListItemText
              primary={workout.type}
              secondary={`Duration: ${workout.duration} minutes | Calories Burned: ${workout.caloriesBurned}`}
            />
            <ListItemSecondaryAction>
              <IconButton edge="end" aria-label="edit" onClick={() => handleEdit(workout._id)}>
                Edit
              </IconButton>
              <IconButton edge="end" aria-label="delete" onClick={() => handleDelete(workout._id)}>
                <DeleteIcon />
              </IconButton>
            </ListItemSecondaryAction>
          </ListItem>
        ))}
      </List>
      <AddWorkout open={addWorkoutOpen} handleClose={() => setAddWorkoutOpen(false)} onWorkoutAdded={fetchWorkouts} />
      {currentWorkoutId && (
        <EditWorkout open={editWorkoutOpen} handleClose={() => setEditWorkoutOpen(false)} workoutId={currentWorkoutId} onWorkoutUpdated={fetchWorkouts} />
      )}
    </Container>
  );
};

export default Workouts;
