import React, { useState } from 'react';
import { Container, Typography, Grid, Card, CardContent, Button, TextField, LinearProgress, AppBar, Toolbar, IconButton } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';
import { useNavigate } from 'react-router-dom';

const Goals: React.FC = () => {
  const [fitnessGoal, setFitnessGoal] = useState('');
  const [progress, setProgress] = useState(50); // Progrès de l'utilisateur
  const navigate = useNavigate();

  const handleGoalSubmit = () => {
    console.log(`New goal set: ${fitnessGoal}`);
    setFitnessGoal('');
  };

  const handleHomeClick = () => {
    navigate('/');
  };

  return (
    <div style={{ 
      height: '100vh', 
      backgroundColor: '#000', 
      color: '#fff', 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center', 
      padding: '20px', 
      flexDirection: 'column'
    }}>

      {/* Navbar */}
      <AppBar position="static" sx={{ backgroundColor: '#FF9800', marginBottom: '20px' }}>
        <Toolbar>
          <IconButton edge="start" color="inherit" onClick={handleHomeClick} aria-label="home">
            <HomeIcon />
          </IconButton>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Goals
          </Typography>
        </Toolbar>
      </AppBar>

      <Container 
        sx={{
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          color: '#fff',
          padding: '20px',
          backgroundColor: 'rgba(255, 255, 255, 0.1)', 
          borderRadius: '10px',
        }}
      >
        <Typography variant="h4" gutterBottom>
          Set Your Fitness Goals
        </Typography>

        {/* Form to set new goals */}
        <TextField
          label="Your Fitness Goal"
          fullWidth
          value={fitnessGoal}
          onChange={(e) => setFitnessGoal(e.target.value)}
          sx={{ backgroundColor: 'rgba(192, 192, 192, 0.8)', borderRadius: '5px', marginBottom: '20px' }}
        />

        <Button 
          variant="contained" 
          sx={{ backgroundColor: '#FF9800', color: '#fff', '&:hover': { backgroundColor: '#e68900' } }}
          onClick={handleGoalSubmit}
        >
          Set Goal
        </Button>

        {/* Progress section */}
        <Typography variant="h6" gutterBottom sx={{ marginTop: '40px' }}>
          Your Progress
        </Typography>

        <Card sx={{ width: '100%', backgroundColor: '#333', marginBottom: '20px' }}>
          <CardContent>
            <Typography variant="body1" sx={{ color: '#ccc' }}>
              You're 50% of the way to your weekly goal!
            </Typography>
            <LinearProgress variant="determinate" value={progress} sx={{ marginTop: '10px' }} />
          </CardContent>
        </Card>
      </Container>
    </div>
  );
};

export default Goals;
