import React, { useState } from 'react';
import { TextField, Button, Container, Typography } from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import BackgroundImage from '../../assets/background.jpg';

const Login: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    console.log('Form submitted'); // Vérifiez que la fonction est appelée
    console.log('Email:', email, 'Password:', password);

    try {
      console.log('Sending login request to server...');
      const response = await axios.post('http://192.168.30.82:5003/auth/login', { email, password });
      console.log('Server response:', response.data);

      const username = response.data.user?.username;
      if (username) {
        console.log('Navigating to profile page:', `/profile/${username}`);
        navigate(`/profile/${username}`);
      } else {
        console.error('Username is missing in server response');
      }
    } catch (error) {
      console.error('Erreur de connexion :', error);
    }
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      backgroundImage: `url(${BackgroundImage})`, 
      backgroundSize: 'cover', 
      backgroundRepeat: 'no-repeat', 
      backgroundPosition: 'center', 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center' 
    }}>
      <Container 
        maxWidth="sm" 
        sx={{
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          color: '#fff',
          padding: '20px',
          backgroundColor: 'rgba(0, 0, 0, 0.6)',
          borderRadius: '10px',
        }}
      >
        <Typography variant="h4" gutterBottom>
          Login
        </Typography>
        <form onSubmit={handleSubmit} style={{ width: '100%' }}>
          <TextField
            label="Email"
            type="email"
            fullWidth
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            sx={{ marginBottom: '16px' }}
          />
          <TextField
            label="Password"
            type="password"
            fullWidth
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            sx={{ marginBottom: '20px' }}
          />
          <Button type="submit" variant="contained" fullWidth>
            Login
          </Button>
        </form>
      </Container>
    </div>
  );
};

export default Login;
