import React, { useState } from 'react';
import { Container, Typography, TextField, Button, List, ListItem, ListItemText, IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

interface Meal {
  name: string;
  calories: number;
}

const dailyCalorieLimit = 2000; // Limite de calories par jour

const Nutrition: React.FC = () => {
  const [mealName, setMealName] = useState('');
  const [calories, setCalories] = useState<number | string>('');
  const [meals, setMeals] = useState<Meal[]>([]);
  const [totalCalories, setTotalCalories] = useState(0);

  const handleAddMeal = () => {
    if (!mealName || !calories) return;

    const mealCalories = parseInt(calories as string);
    const newMeal: Meal = { name: mealName, calories: mealCalories };

    setMeals([...meals, newMeal]);
    setTotalCalories(totalCalories + mealCalories);
    setMealName('');
    setCalories('');
  };

  const handleDeleteMeal = (index: number) => {
    const mealToDelete = meals[index];
    setMeals(meals.filter((_, i) => i !== index)); // Supprimer le repas de la liste
    setTotalCalories(totalCalories - mealToDelete.calories); // Mettre à jour le total des calories
  };

  const remainingCalories = dailyCalorieLimit - totalCalories;

  return (
    <Container sx={{ backgroundColor: '#222', padding: '20px', borderRadius: '8px', color: '#fff' }}>
      <Typography variant="h4" gutterBottom>Daily Nutrition Tracker</Typography>

      <TextField
        label="Meal Name"
        fullWidth
        value={mealName}
        onChange={(e) => setMealName(e.target.value)}
        sx={{ marginBottom: '16px', backgroundColor: '#fff', borderRadius: '4px' }}
      />

      <TextField
        label="Calories"
        fullWidth
        type="number"
        value={calories}
        onChange={(e) => setCalories(e.target.value)}
        sx={{ marginBottom: '16px', backgroundColor: '#fff', borderRadius: '4px' }}
      />

      <Button 
        variant="contained" 
        onClick={handleAddMeal}
        sx={{ backgroundColor: '#FF9800', color: '#fff', '&:hover': { backgroundColor: '#e68900' } }}
      >
        Add Meal
      </Button>

      <Typography variant="h6" sx={{ marginTop: '20px' }}>
        Remaining Calories: {remainingCalories < 0 ? `Over by ${-remainingCalories}` : remainingCalories}
      </Typography>

      <List>
        {meals.map((meal, index) => (
          <ListItem key={index} 
            secondaryAction={
              <IconButton edge="end" aria-label="delete" onClick={() => handleDeleteMeal(index)}>
                <DeleteIcon sx={{ color: '#FF9800' }} />
              </IconButton>
            }
          >
            <ListItemText primary={`${meal.name} - ${meal.calories} calories`} />
          </ListItem>
        ))}
      </List>
    </Container>
  );
};

export default Nutrition;
