import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TextField, Button, Dialog, DialogActions, DialogContent, DialogTitle } from '@mui/material';

interface EditWorkoutProps {
  open: boolean;
  handleClose: () => void;
  workoutId: string;
  onWorkoutUpdated: () => void;
}

const EditWorkout: React.FC<EditWorkoutProps> = ({ open, handleClose, workoutId, onWorkoutUpdated }) => {
  const [type, setType] = useState('');
  const [duration, setDuration] = useState('');
  const [caloriesBurned, setCaloriesBurned] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:5000/workouts/${workoutId}`)
      .then(response => {
        const { type, duration, caloriesBurned } = response.data;
        setType(type);
        setDuration(duration);
        setCaloriesBurned(caloriesBurned);
      })
      .catch(error => {
        console.error('There was an error fetching the workout!', error);
      });
  }, [workoutId]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const updatedWorkout = { type, duration: Number(duration), caloriesBurned: Number(caloriesBurned) };

    axios.put(`http://localhost:5000/workouts/${workoutId}`, updatedWorkout)
      .then(() => {
        onWorkoutUpdated();
        handleClose();
      })
      .catch(error => {
        console.error('There was an error updating the workout!', error);
      });
  };

  return (
    <Dialog open={open} onClose={handleClose}>
      <DialogTitle>Edit Workout</DialogTitle>
      <DialogContent>
        <form onSubmit={handleSubmit}>
          <TextField
            label="Type"
            fullWidth
            margin="normal"
            value={type}
            onChange={(e) => setType(e.target.value)}
          />
          <TextField
            label="Duration (minutes)"
            type="number"
            fullWidth
            margin="normal"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
          />
          <TextField
            label="Calories Burned"
            type="number"
            fullWidth
            margin="normal"
            value={caloriesBurned}
            onChange={(e) => setCaloriesBurned(e.target.value)}
          />
          <DialogActions>
            <Button onClick={handleClose} color="secondary">
              Cancel
            </Button>
            <Button type="submit" color="primary">
              Update
            </Button>
          </DialogActions>
        </form>
      </DialogContent>
    </Dialog>
  );
};

export default EditWorkout;
