import React, { useState } from 'react';
import { TextField, Button, Container, Typography } from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';   

import BackgroundImage from '../../assets/background.jpg'; // Assurez-vous que le chemin est correct

const Login: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();


  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5003/auth/login', { email, password });
      const username = response.data.user.username; // Récupérez le nom d'utilisateur
      navigate(`/profile/${username}`);
    } catch (error) {
      console.error('Erreur de connexion :', error);
    }
  };



  return (
    <div style={{ 
      height: '100vh', 
      backgroundImage: `url(${BackgroundImage})`, 
      backgroundSize: 'cover', 
      backgroundRepeat: 'no-repeat', 
      backgroundPosition: 'center', 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center' 
    }}>
      <Container 
        maxWidth="sm" 
        sx={{
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          color: '#fff',
          padding: '20px',
          backgroundColor: 'rgba(0, 0, 0, 0.6)', // Une légère opacité pour le conteneur
          borderRadius: '10px',
        }}
      >
        <Typography variant="h4" component="h1" gutterBottom>
          Login
        </Typography>
        <form onSubmit={handleSubmit} style={{ width: '100%' }}>
          <TextField
            label="Email"
            type="email"
            fullWidth
            margin="normal"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            sx={{ backgroundColor: 'rgba(192, 192, 192, 0.8)', borderRadius: '5px' }}
          />
          <TextField
            label="Password"
            type="password"
            fullWidth
            margin="normal"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            sx={{ backgroundColor: 'rgba(192, 192, 192, 0.8)', borderRadius: '5px' }}
          />
          <Button 
            type="submit" 
            variant="contained" 
            sx={{ 
              marginTop: '20px', 
              backgroundColor: '#FF9800', 
              color: '#fff', 
              '&:hover': { backgroundColor: '#e68900' } 
            }}
            fullWidth
          >
            Login
          </Button>
        </form>
      </Container>
    </div>
  );
};

export default Login;
