import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

const ProfilePage = () => {
  const { username } = useParams(); // Récupérer le nom d'utilisateur depuis l'URL
  const [user, setUser] = useState({ name: username }); // État pour stocker les informations de l'utilisateur

  // Simuler la récupération des données de l'utilisateur depuis un serveur
  // Remplacer cette partie par votre logique d'authentification
  // useEffect(() => {
  //   fetch(`/api/users/${username}`)
  //     .then(response => response.json())
  //     .then(data => setUser(data));
  // }, [username]);

  return (
    <div>
      <h1>Bonjour {user.name}, heureux de vous revoir !</h1>
      <p>En quoi puis-je vous aider aujourd'hui ?</p>
      <ul>
        <li>
          <a href="/workout">Workout</a>
        </li>
        <li>
          <a href="/nutrition">Nutrition</a>
        </li>
        <li>
          <a href="/recipe-ideas">Recettes</a>
        </li>
      </ul>
    </div>
  );
};

export default ProfilePage;
