// src/components/BodyTypeSelection.tsx
import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Typography, Grid, Card, CardMedia, CardActionArea } from '@mui/material';

const BodyTypeSelection: React.FC = () => {
  const { gender } = useParams<{ gender: string }>();
  const navigate = useNavigate();

  const bodyTypes = [
    { type: 'ectomorph', image: '/images/ectomorph.png' },
    { type: 'mesomorph', image: '/images/mesomorph.jpeg' },
    { type: 'endomorph', image: '/images/endomorph.png' },
  ];

  const handleBodyTypeSelection = (bodyType: string) => {
    navigate(`/exercises/${gender}/${bodyType}`);
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Choisissez votre type de corps
      </Typography>
      <Grid container spacing={3}>
        {bodyTypes.map((bodyType) => (
          <Grid item xs={12} sm={4} key={bodyType.type}>
            <Card>
              <CardActionArea onClick={() => handleBodyTypeSelection(bodyType.type)}>
                <CardMedia component="img" alt={bodyType.type} height="140" image={bodyType.image} />
              </CardActionArea>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default BodyTypeSelection;
