import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Typography, List, ListItem, ListItemText, ListItemSecondaryAction, IconButton, Button } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddGoal from './AddGoal';
import EditGoal from './EditGoal';

interface Goal {
  _id: string;
  title: string;
  description: string;
  targetDate: string;
}

const Goals: React.FC = () => {
  const [goals, setGoals] = useState<Goal[]>([]);
  const [addGoalOpen, setAddGoalOpen] = useState(false);
  const [editGoalOpen, setEditGoalOpen] = useState(false);
  const [currentGoalId, setCurrentGoalId] = useState<string | null>(null);

  useEffect(() => {
    fetchGoals();
  }, []);

  const fetchGoals = () => {
    axios.get('http://localhost:5000/goals')
      .then(response => {
        setGoals(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the goals!', error);
      });
  };

  const handleDelete = (id: string) => {
    axios.delete(`http://localhost:5000/goals/${id}`)
      .then(() => {
        fetchGoals();
      })
      .catch(error => {
        console.error('There was an error deleting the goal!', error);
      });
  };

  const handleEdit = (id: string) => {
    setCurrentGoalId(id);
    setEditGoalOpen(true);
  };

  return (
    <Container>
      <Typography variant="h4" component="h1" gutterBottom>
        Goals
      </Typography>
      <Button variant="contained" color="primary" onClick={() => setAddGoalOpen(true)}>
        Add Goal
      </Button>
      <List>
        {goals.map(goal => (
          <ListItem key={goal._id}>
            <ListItemText
              primary={goal.title}
              secondary={`${goal.description} - Target Date: ${new Date(goal.targetDate).toLocaleDateString()}`}
            />
            <ListItemSecondaryAction>
              <IconButton edge="end" aria-label="edit" onClick={() => handleEdit(goal._id)}>
                Edit
              </IconButton>
              <IconButton edge="end" aria-label="delete" onClick={() => handleDelete(goal._id)}>
                <DeleteIcon />
              </IconButton>
            </ListItemSecondaryAction>
          </ListItem>
        ))}
      </List>
      <AddGoal open={addGoalOpen} handleClose={() => setAddGoalOpen(false)} onGoalAdded={fetchGoals} />
      {currentGoalId && (
        <EditGoal open={editGoalOpen} handleClose={() => setEditGoalOpen(false)} goalId={currentGoalId} onGoalUpdated={fetchGoals} />
      )}
    </Container>
  );
};

export default Goals;
