import React, { useState } from 'react';
import { Container, Typography, Button, Grid, Select, MenuItem, FormControl, InputLabel } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const LandingPage: React.FC = () => {
  const [ageRange, setAgeRange] = useState('');
  const [bodyType, setBodyType] = useState('');
  const navigate = useNavigate();

  const handleStart = () => {
    // Vous pouvez enregistrer les choix de l'utilisateur dans un contexte global ou les passer dans l'URL pour l'utiliser sur la page suivante.
    navigate('/signin');
  };

  return (
    <Container sx={{ height: '100vh', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
      <Typography variant="h4" gutterBottom>
        Bienvenue sur Fitness Tracker
      </Typography>
      <Typography variant="h6" gutterBottom>
        Veuillez choisir votre tranche d'âge et votre type de corps
      </Typography>

      <Grid container spacing={3} sx={{ marginTop: '20px' }}>
        <Grid item xs={12} sm={6}>
          <FormControl fullWidth>
            <InputLabel>Tranche d'âge</InputLabel>
            <Select value={ageRange} onChange={(e) => setAgeRange(e.target.value)}>
              <MenuItem value="18-25">18-25 ans</MenuItem>
              <MenuItem value="26-35">26-35 ans</MenuItem>
              <MenuItem value="36-45">36-45 ans</MenuItem>
              <MenuItem value="46+">46 ans et plus</MenuItem>
            </Select>
          </FormControl>
        </Grid>

        <Grid item xs={12} sm={6}>
          <FormControl fullWidth>
            <InputLabel>Type de corps</InputLabel>
            <Select value={bodyType} onChange={(e) => setBodyType(e.target.value)}>
              <MenuItem value="ectomorph">Ectomorphe</MenuItem>
              <MenuItem value="mesomorph">Mésomorphe</MenuItem>
              <MenuItem value="endomorph">Endomorphe</MenuItem>
            </Select>
          </FormControl>
        </Grid>
      </Grid>

      <Button 
        variant="contained" 
        color="primary" 
        onClick={handleStart} 
        sx={{ marginTop: '30px' }}
        disabled={!ageRange || !bodyType} // Désactiver le bouton si les choix ne sont pas faits
      >
        Commencer
      </Button>
    </Container>
  );
};

export default LandingPage;
